(1422, 47, 2, 134, 0, 0, 4, 'Le loup s''allongea à l''ombre d''un chêne et se mit aussitôt à ronfler comme un bien heureux.\n\nSatisfait, repu et la peau du ventre bien tendu !', '', 0, 0, 1, 0, 1376, 0, 0, 'undefined', 0, 0, 0, 0, '0000-00-00 00:00:00', 0, 1, '2012-03-12 22:28:25', '2013-11-30 04:17:22'),
(1424, 49, 2, 134, 0, 0, 12, 'Le loup sourit et répondit...', '', 0, 0, 0, 0, 1384, 0, 0, 'undefined', 0, 0, 0, 0, '0000-00-00 00:00:00', 0, 1, '2012-03-12 22:56:55', '2013-11-30 04:17:24'),
